        const random = Math.floor(Math.random() * posibilities.length)
        console.log(random)
        setResult(posibilities[random])
    }

    useEffect(() => {
        handleClose
    }, [])

    return(
        <div className="modal-container">
                    <div className="modal">
                        <div className='modal-content'>
                            <h1>{result}</h1>
                            <button onClick={() => setModal(false)}>‚ùå</button>
                        </div>
                </div>
            </div> 
    )
}

export default Modal
